contracts/bluecheck/BlueCheckRegistry.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title BlueCheckRegistry
 * @notice Handles Blue Check purchases with automatic fee splitting
 * @dev $10 USD payment with 70% to Treasury, 30% to Referral Pool
 */
contract BlueCheckRegistry is Ownable, ReentrancyGuard, Pausable {
    // ===================== Constants =====================

    /// @notice Price in USD (18 decimals, like wei)
    uint256 public constant PRICE_USD = 10 * 1e18; // $10

    /// @notice Fee distribution percentages
    uint256 public constant TREASURY_SHARE = 70; // 70%
    uint256 public constant REFERRAL_POOL_SHARE = 30; // 30%

    // ===================== State Variables =====================

    /// @notice Treasury address (receives 70%)
    address public treasury;

    /// @notice Referral pool address (receives 30%)
    address public referralPool;

    /// @notice Price oracle address (for BNB/USD conversion)
    address public priceOracle;

    /// @notice Manual price in BNB (18 decimals) - fallback if oracle fails
    uint256 public manualPriceBNB;

    /// @notice Track who has purchased Blue Check
    mapping(address => bool) public hasBlueCheck;

    /// @notice Track purchase timestamps
    mapping(address => uint256) public purchaseTimestamp;

    /// @notice Total purchases counter
    uint256 public totalPurchases;

    /// @notice Total BNB collected
    uint256 public totalBNBCollected;

    // ===================== Events =====================

    /**
     * @notice Emitted when Blue Check is purchased
     * @param user Address of the purchaser
     * @param amountPaid BNB amount paid
     * @param treasuryAmount BNB sent to treasury
     * @param referralPoolAmount BNB sent to referral pool
     * @param timestamp Purchase timestamp
     */
    event BlueCheckPurchased(
        address indexed user,
        uint256 amountPaid,
        uint256 treasuryAmount,
        uint256 referralPoolAmount,
        uint256 timestamp
    );

    /**
     * @notice Emitted when treasury address is updated
     * @param oldTreasury Previous treasury address
     * @param newTreasury New treasury address
     */
    event TreasuryUpdated(address indexed oldTreasury, address indexed newTreasury);

    /**
     * @notice Emitted when referral pool address is updated
     * @param oldPool Previous pool address
     * @param newPool New pool address
     */
    event ReferralPoolUpdated(address indexed oldPool, address indexed newPool);

    /**
     * @notice Emitted when manual price is updated
     * @param oldPrice Previous price in BNB
     * @param newPrice New price in BNB
     */
    event ManualPriceUpdated(uint256 oldPrice, uint256 newPrice);

    // ===================== Errors =====================

    error AlreadyPurchased();
    error InsufficientPayment();
    error InvalidAddress();
    error TransferFailed();

    // ===================== Constructor =====================

    /**
     * @notice Initialize the contract
     * @param _treasury Treasury address
     * @param _referralPool Referral pool address
     * @param _initialPriceBNB Initial BNB price (fallback)
     */
    constructor(
        address _treasury,
        address _referralPool,
        uint256 _initialPriceBNB
    ) Ownable(msg.sender) {
        if (_treasury == address(0) || _referralPool == address(0)) {
            revert InvalidAddress();
        }

        treasury = _treasury;
        referralPool = _referralPool;
        manualPriceBNB = _initialPriceBNB;
    }

    // ===================== Main Functions =====================

    /**
     * @notice Purchase Blue Check (lifetime access)
     * @dev Sends 70% to treasury, 30% to referral pool
     */
    function purchaseBlueCheck() external payable nonReentrant whenNotPaused {
        if (hasBlueCheck[msg.sender]) {
            revert AlreadyPurchased();
        }

        // Get required BNB amount
        uint256 requiredBNB = getRequiredBNB();

        if (msg.value < requiredBNB) {
            revert InsufficientPayment();
        }

        // Mark as purchased
        hasBlueCheck[msg.sender] = true;
        purchaseTimestamp[msg.sender] = block.timestamp;
        totalPurchases++;
        totalBNBCollected += requiredBNB;

        // Calculate splits
        uint256 treasuryAmount = (requiredBNB * TREASURY_SHARE) / 100;
        uint256 referralAmount = requiredBNB - treasuryAmount; // Ensures no rounding issues

        // Transfer to treasury
        (bool treasurySuccess, ) = treasury.call{value: treasuryAmount}("");
        if (!treasurySuccess) {
            revert TransferFailed();
        }

        // Transfer to referral pool
        (bool poolSuccess, ) = referralPool.call{value: referralAmount}("");
        if (!poolSuccess) {
            revert TransferFailed();
        }

        // Emit event for indexer
        emit BlueCheckPurchased(
            msg.sender,
            requiredBNB,
            treasuryAmount,
            referralAmount,
            block.timestamp
        );

        // Refund excess if any
        uint256 excess = msg.value - requiredBNB;
        if (excess > 0) {
            (bool refundSuccess, ) = msg.sender.call{value: excess}("");
            if (!refundSuccess) {
                revert TransferFailed();
            }
        }
    }

    /**
     * @notice Get required BNB for $10 USD
     * @dev Uses manual price (oracle integration can be added later)
     * @return Required BNB amount in wei
     */
    function getRequiredBNB() public view returns (uint256) {
        // TODO: Integrate with Chainlink or price oracle
        // For now, use manual price

        if (manualPriceBNB == 0) {
            revert("Price not set");
        }

        // manualPriceBNB is price of 1 BNB in USD (18 decimals)
        // PRICE_USD is $10 in 18 decimals
        // Required BNB = PRICE_USD / manualPriceBNB

        return (PRICE_USD * 1e18) / manualPriceBNB;
    }

    /**
     * @notice Check if address has Blue Check
     * @param user Address to check
     * @return bool True if user has Blue Check
     */
    function checkBlueCheck(address user) external view returns (bool) {
        return hasBlueCheck[user];
    }

    // ===================== Admin Functions =====================

    /**
     * @notice Update treasury address
     * @param _newTreasury New treasury address
     */
    function updateTreasury(address _newTreasury) external onlyOwner {
        if (_newTreasury == address(0)) {
            revert InvalidAddress();
        }

        address oldTreasury = treasury;
        treasury = _newTreasury;

        emit TreasuryUpdated(oldTreasury, _newTreasury);
    }

    /**
     * @notice Update referral pool address
     * @param _newPool New pool address
     */
    function updateReferralPool(address _newPool) external onlyOwner {
        if (_newPool == address(0)) {
            revert InvalidAddress();
        }

        address oldPool = referralPool;
        referralPool = _newPool;

        emit ReferralPoolUpdated(oldPool, _newPool);
    }

    /**
     * @notice Update manual BNB price
     * @param _newPriceBNB New price (1 BNB in USD, 18 decimals)
     * @dev Example: If 1 BNB = $600, pass 600 * 1e18
     */
    function updateManualPrice(uint256 _newPriceBNB) external onlyOwner {
        uint256 oldPrice = manualPriceBNB;
        manualPriceBNB = _newPriceBNB;

        emit ManualPriceUpdated(oldPrice, _newPriceBNB);
    }

    /**
     * @notice Pause contract (emergency)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpause contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @notice Emergency withdraw (only if stuck funds)
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(uint256 amount) external onlyOwner {
        (bool success, ) = owner().call{value: amount}("");
        if (!success) {
            revert TransferFailed();
        }
    }
}

contracts/fairlaunch/Fairlaunch.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

// Uniswap V2 interfaces (compatible with PancakeSwap)
interface IUniswapV2Router02 {
    function factory() external pure returns (address);
    function WETH() external pure returns (address);

    function addLiquidityETH(
        address token,
        uint amountTokenDesired,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external payable returns (uint amountToken, uint amountETH, uint liquidity);

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint amountADesired,
        uint amountBDesired,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB, uint liquidity);
}

interface IUniswapV2Factory {
    function getPair(address tokenA, address tokenB) external view returns (address pair);
}

interface IFeeSplitter {
    function distributeFairlaunchFee(address fairlaunch) external payable;
}

interface ILPLocker {
    function lockTokens(address lpToken, uint256 amount, uint256 unlockTime) external;
}

/**
 * @title Fairlaunch
 * @notice Fair price discovery launchpad pool with automated liquidity provision
 * @dev Price = total_raised / tokens_for_sale (no hardcap)
 */
contract Fairlaunch is AccessControl, ReentrancyGuard {
    using SafeERC20 for IERC20;

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    enum Status {
        UPCOMING,   // Before startTime
        LIVE,       // During sale period
        ENDED,      // Sale ended, awaiting finalization
        SUCCESS,    // Finalized successfully
        FAILED,     // Softcap not met
        CANCELLED   // Admin cancelled
    }

    // Core config (immutable)
    address public immutable projectToken;
    address public immutable paymentToken; // address(0) for native
    uint256 public immutable softcap;
    uint256 public immutable tokensForSale;
    uint256 public immutable minContribution;
    uint256 public immutable maxContribution;
    uint256 public immutable startTime;
    uint256 public immutable endTime;
    uint16 public immutable listingPremiumBps; // e.g., 500 = 5%

    // External contracts
    address public immutable feeSplitter;
    address public immutable teamVesting; // Can be address(0)
    address public immutable projectOwner;
    IUniswapV2Router02 public immutable dexRouter;
    ILPLocker public lpLocker; // Set during finalization

    // LP config
    uint256 public immutable liquidityPercent; // BPS, min 7000 (70%)
    uint256 public immutable lpLockMonths;     // Min 12 months
    bytes32 public immutable dexId;

    // State
    Status public status;
    uint256 public totalRaised;
    uint256 public participantCount;
    uint256 public finalTokenPrice; // Set on finalization
    bool public isFinalized;
    bool public isPaused;

    mapping(address => uint256) public contributions;
    mapping(address => bool) public hasClaimed;

    // Constants
    uint256 private constant PLATFORM_FEE_BPS = 500; // 5%
    uint256 private constant BPS_BASE = 10000;

    // Events
    event Contributed(address indexed user, uint256 amount, uint256 totalRaised);
    event FinalizedSuccess(uint256 finalPrice, uint256 totalRaised, address lpToken, uint256 lpAmount);
    event FinalizedFail();
    event TokensClaimed(address indexed user, uint256 amount);
    event Refunded(address indexed user, uint256 amount);
    event Paused();
    event Unpaused();
    event Cancelled();
    event LiquidityAdded(address indexed lpToken, uint256 lpAmount, uint256 unlockTime);

    // Errors
    error InvalidStatus();
    error ContractPaused();
    error ContributionTooLow();
    error ContributionTooHigh();
    error NoContribution();
    error AlreadyClaimed();
    error NothingToRefund();
    error NotAuthorized();
    error TransferFailed();
    error SoftcapNotMet();

    constructor(
        address _projectToken,
        address _paymentToken,
        uint256 _softcap,
        uint256 _tokensForSale,
        uint256 _minContribution,
        uint256 _maxContribution,
        uint256 _startTime,
        uint256 _endTime,
        uint16 _listingPremiumBps,
        address _feeSplitter,
        address _teamVesting,
        address _projectOwner,
        address _adminExecutor,
        uint256 _liquidityPercent,
        uint256 _lpLockMonths,
        bytes32 _dexId
    ) {
        projectToken = _projectToken;
        paymentToken = _paymentToken;
        softcap = _softcap;
        tokensForSale = _tokensForSale;
        minContribution = _minContribution;
        maxContribution = _maxContribution;
        startTime = _startTime;
        endTime = _endTime;
        listingPremiumBps = _listingPremiumBps;
        feeSplitter = _feeSplitter;
        teamVesting = _teamVesting;
        projectOwner = _projectOwner;
        liquidityPercent = _liquidityPercent;
        lpLockMonths = _lpLockMonths;
        dexId = _dexId;

        status = Status.UPCOMING;

        // Set DEX router based on chain (using ternary for immutable)
        dexRouter = (block.chainid == 56 || block.chainid == 97)
            ? IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E) // BSC / BSC Testnet
            : (block.chainid == 1 || block.chainid == 11155111)
            ? IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D) // Ethereum / Sepolia
            : (block.chainid == 8453 || block.chainid == 84532)
            ? IUniswapV2Router02(0x4752ba5DBc23f44D87826276BF6Fd6b1C372aD24) // Base / Base Sepolia
            : IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D); // Default to Uniswap

        _grantRole(DEFAULT_ADMIN_ROLE, _adminExecutor);
        _grantRole(ADMIN_ROLE, _adminExecutor);
    }

    /**
     * @notice Contribute native token (BNB/ETH)
     */
    function contribute() external payable nonReentrant {
        if (paymentToken != address(0)) revert InvalidStatus();
        _processContribution(msg.sender, msg.value);
    }

    /**
     * @notice Contribute ERC20 token
     */
    function contributeERC20(uint256 amount) external nonReentrant {
        if (paymentToken == address(0)) revert InvalidStatus();

        IERC20(paymentToken).safeTransferFrom(msg.sender, address(this), amount);
        _processContribution(msg.sender, amount);
    }

    /**
     * @notice Internal contribution processing
     */
    function _processContribution(address user, uint256 amount) internal {
        // Update status if needed
        _updateStatus();

        // Validate state
        if (status != Status.LIVE) revert InvalidStatus();
        if (isPaused) revert ContractPaused();
        if (amount < minContribution) revert ContributionTooLow();

        uint256 newTotal = contributions[user] + amount;
        if (newTotal > maxContribution) revert ContributionTooHigh();

        // Track new participants
        if (contributions[user] == 0) {
            participantCount++;
        }

        // Update state
        contributions[user] = newTotal;
        totalRaised += amount;

        emit Contributed(user, amount, totalRaised);
    }

    /**
     * @notice Finalize the Fairlaunch (callable by anyone after endTime)
     */
    function finalize() external nonReentrant {
        _updateStatus();

        if (status != Status.ENDED) revert InvalidStatus();
        if (isFinalized) revert InvalidStatus();

        isFinalized = true;

        // Check if softcap met
        if (totalRaised < softcap) {
            status = Status.FAILED;
            emit FinalizedFail();
            return;
        }

        // Calculate final price
        finalTokenPrice = (totalRaised * 1e18) / tokensForSale;

        // Deduct 5% platform fee
        uint256 platformFee = (totalRaised * PLATFORM_FEE_BPS) / BPS_BASE;
        uint256 netRaised = totalRaised - platformFee;

        // Send fee to FeeSplitter
        if (paymentToken == address(0)) {
            IFeeSplitter(feeSplitter).distributeFairlaunchFee{value: platformFee}(address(this));
        } else {
            IERC20(paymentToken).safeTransfer(feeSplitter, platformFee);
            // Note: FeeSplitter needs ERC20 handling function
        }

        // Calculate liquidity amounts
        uint256 liquidityFunds = (netRaised * liquidityPercent) / BPS_BASE;
        uint256 liquidityTokens = (tokensForSale * liquidityPercent) / BPS_BASE;

        // Add liquidity to DEX
        address lpToken = _addLiquidity(liquidityTokens, liquidityFunds);

        // Lock LP tokens (implementation depends on locker provider)
        uint256 lpBalance = IERC20(lpToken).balanceOf(address(this));
        uint256 unlockTime = block.timestamp + (lpLockMonths * 30 days);

        // TODO: Integrate with actual LP locker
        // For now, transfer to project owner (UNSAFE - replace with locker)
        IERC20(lpToken).safeTransfer(projectOwner, lpBalance);

        emit LiquidityAdded(lpToken, lpBalance, unlockTime);

        // Transfer remaining tokens to team vesting (if exists)
        if (teamVesting != address(0)) {
            uint256 remainingTokens = IERC20(projectToken).balanceOf(address(this)) - tokensForSale;
            if (remainingTokens > 0) {
                IERC20(projectToken).safeTransfer(teamVesting, remainingTokens);
            }
        }

        // Transfer remaining raised funds to project owner
        uint256 remainingFunds = netRaised - liquidityFunds;
        if (paymentToken == address(0)) {
            (bool success, ) = projectOwner.call{value: remainingFunds}("");
            if (!success) revert TransferFailed();
        } else {
            IERC20(paymentToken).safeTransfer(projectOwner, remainingFunds);
        }

        status = Status.SUCCESS;
        emit FinalizedSuccess(finalTokenPrice, totalRaised, lpToken, lpBalance);
    }

    /**
     * @notice Add liquidity to DEX
     */
    function _addLiquidity(uint256 tokenAmount, uint256 fundAmount) internal returns (address lpToken) {
        // Approve router
        IERC20(projectToken).approve(address(dexRouter), tokenAmount);

        if (paymentToken == address(0)) {
            // Native pair (ETH/BNB)
            dexRouter.addLiquidityETH{value: fundAmount}(
                projectToken,
                tokenAmount,
                0, // Accept any amount of tokens
                0, // Accept any amount of ETH
                address(this),
                block.timestamp + 300
            );

            lpToken = IUniswapV2Factory(dexRouter.factory()).getPair(
                projectToken,
                dexRouter.WETH()
            );
        } else {
            // ERC20 pair
            IERC20(paymentToken).approve(address(dexRouter), fundAmount);

            dexRouter.addLiquidity(
                projectToken,
                paymentToken,
                tokenAmount,
                fundAmount,
                0,
                0,
                address(this),
                block.timestamp + 300
            );

            lpToken = IUniswapV2Factory(dexRouter.factory()).getPair(
                projectToken,
                paymentToken
            );
        }
    }

    /**
     * @notice Claim tokens after successful finalization
     */
    function claimTokens() external nonReentrant {
        if (status != Status.SUCCESS) revert InvalidStatus();
        if (contributions[msg.sender] == 0) revert NoContribution();
        if (hasClaimed[msg.sender]) revert AlreadyClaimed();

        // Calculate user's token allocation
        uint256 userTokens = (contributions[msg.sender] * tokensForSale) / totalRaised;

        hasClaimed[msg.sender] = true;

        IERC20(projectToken).safeTransfer(msg.sender, userTokens);

        emit TokensClaimed(msg.sender, userTokens);
    }

    /**
     * @notice Refund contributions if failed or cancelled
     */
    function refund() external nonReentrant {
        if (status != Status.FAILED && status != Status.CANCELLED) revert InvalidStatus();
        if (contributions[msg.sender] == 0) revert NothingToRefund();

        uint256 amount = contributions[msg.sender];
        contributions[msg.sender] = 0;

        if (paymentToken == address(0)) {
            (bool success, ) = msg.sender.call{value: amount}("");
            if (!success) revert TransferFailed();
        } else {
            IERC20(paymentToken).safeTransfer(msg.sender, amount);
        }

        emit Refunded(msg.sender, amount);
    }

    /**
     * @notice Pause contributions (admin only)
     */
    function pause() external onlyRole(ADMIN_ROLE) {
        isPaused = true;
        emit Paused();
    }

    /**
     * @notice Unpause contributions (admin only)
     */
    function unpause() external onlyRole(ADMIN_ROLE) {
        isPaused = false;
        emit Unpaused();
    }

    /**
     * @notice Cancel fairlaunch (admin only, emergency)
     */
    function cancel() external onlyRole(ADMIN_ROLE) {
        if (status == Status.SUCCESS) revert InvalidStatus(); // Cannot cancel after success

        status = Status.CANCELLED;
        emit Cancelled();
    }

    /**
     * @notice Update status based on current time
     */
    function _updateStatus() internal {
        if (status == Status.UPCOMING && block.timestamp >= startTime) {
            status = Status.LIVE;
        } else if (status == Status.LIVE && block.timestamp >= endTime) {
            status = Status.ENDED;
        }
    }

    /**
     * @notice Get current status
     */
    function getStatus() external view returns (Status) {
        if (status == Status.UPCOMING && block.timestamp >= startTime) {
            return Status.LIVE;
        } else if (status == Status.LIVE && block.timestamp >= endTime) {
            return Status.ENDED;
        }
        return status;
    }

    /**
     * @notice Get user's contribution
     */
    function getUserContribution(address user) external view returns (uint256) {
        return contributions[user];
    }

    /**
     * @notice Get user's token allocation (if finalized successfully)
     */
    function getUserAllocation(address user) external view returns (uint256) {
        if (status != Status.SUCCESS) return 0;
        if (totalRaised == 0) return 0;
        return (contributions[user] * tokensForSale) / totalRaised;
    }

    /**
     * @notice Get final price (if finalized)
     */
    function getFinalPrice() external view returns (uint256) {
        return finalTokenPrice;
    }
}

contracts/fairlaunch/FairlaunchFactory.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./Fairlaunch.sol";
import "./TeamVesting.sol";

/**
 * @title FairlaunchFactory
 * @notice Factory for deploying Fairlaunch pools with integrated team vesting
 * @dev Enforces deployment fee, LP lock requirements, and security standards
 */
contract FairlaunchFactory is AccessControl {
    using SafeERC20 for IERC20;

    bytes32 public constant FACTORY_ADMIN_ROLE = keccak256("FACTORY_ADMIN_ROLE");

    // External contract references
    address public immutable feeSplitter;
    address public immutable treasuryWallet;
    address public immutable adminExecutor;

    // Fee structure
    uint256 public immutable DEPLOYMENT_FEE; // 0.2 BNB for BSC, 0.1 ETH for ETH/Base

    // Fairlaunch tracking
    uint256 public fairlaunchCount;
    mapping(uint256 => address) public fairlaunches;
    mapping(address => address) public fairlaunchToVesting;
    mapping(address => LPLockPlan) public fairlaunchToLPPlan;

    struct LPLockPlan {
        uint256 lockMonths;        // Minimum 12 months
        uint256 liquidityPercent;  // Minimum 7000 (70%)
        bytes32 dexId;             // DEX identifier
    }

    struct CreateFairlaunchParams {
        address projectToken;
        address paymentToken;      // address(0) for native
        uint256 softcap;
        uint256 tokensForSale;
        uint256 minContribution;
        uint256 maxContribution;
        uint256 startTime;
        uint256 endTime;
        address projectOwner;
        uint16 listingPremiumBps;
    }

    struct TeamVestingParams {
        address beneficiary;
        uint256 startTime;
        uint256[] durations;
        uint256[] amounts;
    }

    // Events
    event FairlaunchCreated(
        uint256 indexed fairlaunchId,
        address indexed fairlaunch,
        address indexed vesting,
        address projectToken
    );
    event LPPlanRecorded(
        address indexed fairlaunch,
        uint256 lockMonths,
        bytes32 dexId,
        uint256 liquidityPercent
    );

    // Errors
    error InsufficientDeploymentFee();
    error InvalidSoftcap();
    error InvalidTimeRange();
    error InvalidContributionRange();
    error InsufficientLPLockDuration();
    error InsufficientLiquidityPercent();
    error ZeroAddress();
    error FeeTransferFailed();
    error InsufficientTokenBalance();
    error TokenTransferFailed();

    constructor(
        uint256 _deploymentFee,
        address _feeSplitter,
        address _treasuryWallet,
        address _adminExecutor
    ) {
        if (_feeSplitter == address(0)) revert ZeroAddress();
        if (_treasuryWallet == address(0)) revert ZeroAddress();
        if (_adminExecutor == address(0)) revert ZeroAddress();

        DEPLOYMENT_FEE = _deploymentFee;
        feeSplitter = _feeSplitter;
        treasuryWallet = _treasuryWallet;
        adminExecutor = _adminExecutor;

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(FACTORY_ADMIN_ROLE, msg.sender);
    }

    /**
     * @notice Create a new Fairlaunch pool with optional team vesting
     * @dev Permissionless (no KYC) - SC scan gating handled off-chain
     * @param params Fairlaunch configuration
     * @param vestingParams Team vesting schedule (amounts array can be empty for no vesting)
     * @param lpPlan Liquidity lock plan
     * @return fairlaunch Address of deployed Fairlaunch pool
     * @return vesting Address of deployed TeamVesting (address(0) if no vesting)
     */
    function createFairlaunch(
        CreateFairlaunchParams calldata params,
        TeamVestingParams calldata vestingParams,
        LPLockPlan calldata lpPlan
    ) external payable returns (address fairlaunch, address vesting) {
        // Validate deployment fee
        if (msg.value < DEPLOYMENT_FEE) revert InsufficientDeploymentFee();

        // Validate parameters
        _validateParams(params, lpPlan);

        // Send deployment fee to treasury
        (bool success, ) = treasuryWallet.call{value: msg.value}("");
        if (!success) revert FeeTransferFailed();

        // Deploy TeamVesting if amounts provided
        if (vestingParams.amounts.length > 0) {
            vesting = address(new TeamVesting(
                params.projectToken,
                vestingParams.beneficiary,
                vestingParams.startTime,
                vestingParams.durations,
                vestingParams.amounts,
                adminExecutor
            ));
        }

        // Deploy Fairlaunch pool
        fairlaunch = address(new Fairlaunch(
            params.projectToken,
            params.paymentToken,
            params.softcap,
            params.tokensForSale,
            params.minContribution,
            params.maxContribution,
            params.startTime,
            params.endTime,
            params.listingPremiumBps,
            feeSplitter,
            vesting,
            params.projectOwner,
            adminExecutor,
            lpPlan.liquidityPercent,
            lpPlan.lockMonths,
            lpPlan.dexId
        ));

        // Calculate and transfer tokens AFTER deployment
        IERC20 projectToken = IERC20(params.projectToken);

        // Calculate liquidity tokens (multiply before divide for accuracy)
        uint256 liquidityTokens = (params.tokensForSale * lpPlan.liquidityPercent) / 10000;
        uint256 totalFairlaunchTokens = params.tokensForSale + liquidityTokens;

        // Transfer tokens to Fairlaunch contract
        projectToken.safeTransferFrom(msg.sender, fairlaunch, totalFairlaunchTokens);

        // Transfer tokens to Vesting contract if it exists
        if (vesting != address(0)) {
            uint256 totalVestingTokens = _calculateVestingTokens(vestingParams.amounts);
            if (totalVestingTokens > 0) {
                projectToken.safeTransferFrom(msg.sender, vesting, totalVestingTokens);
            }
        }

        // Record mappings
        fairlaunches[fairlaunchCount] = fairlaunch;
        if (vesting != address(0)) {
            fairlaunchToVesting[fairlaunch] = vesting;
        }
        fairlaunchToLPPlan[fairlaunch] = lpPlan;

        // Emit events
        emit FairlaunchCreated(fairlaunchCount, fairlaunch, vesting, params.projectToken);
        emit LPPlanRecorded(fairlaunch, lpPlan.lockMonths, lpPlan.dexId, lpPlan.liquidityPercent);

        fairlaunchCount++;

        return (fairlaunch, vesting);
    }

    /**
     * @notice Validate Fairlaunch parameters
     */
    function _validateParams(
        CreateFairlaunchParams calldata params,
        LPLockPlan calldata lpPlan
    ) internal view {
        // Validate softcap
        if (params.softcap == 0) revert InvalidSoftcap();

        // Validate time
        if (params.startTime >= params.endTime) revert InvalidTimeRange();
        if (params.startTime <= block.timestamp) revert InvalidTimeRange();

        // Validate contribution limits
        if (params.minContribution > params.maxContribution) revert InvalidContributionRange();
        if (params.minContribution == 0) revert InvalidContributionRange();

        // Validate LP lock (minimum 12 months)
        if (lpPlan.lockMonths < 12) revert InsufficientLPLockDuration();

        // Validate liquidity percent (minimum 70%)
        if (lpPlan.liquidityPercent < 7000) revert InsufficientLiquidityPercent();

        // Validate addresses
        if (params.projectToken == address(0)) revert ZeroAddress();
        if (params.projectOwner == address(0)) revert ZeroAddress();
    }

    /**
     * @notice Calculate total vesting tokens from amounts array
     * @dev Returns 0 if array is empty (safe for no-vesting case)
     */
    function _calculateVestingTokens(uint256[] calldata amounts) 
        internal 
        pure 
        returns (uint256 total) 
    {
        for (uint256 i = 0; i < amounts.length; i++) {
            total += amounts[i];
        }
        return total;
    }

    /**
     * @notice Get Fairlaunch details
     */
    function getFairlaunchDetails(uint256 fairlaunchId)
        external
        view
        returns (
            address fairlaunch,
            address vesting,
            LPLockPlan memory lpPlan
        )
    {
        fairlaunch = fairlaunches[fairlaunchId];
        vesting = fairlaunchToVesting[fairlaunch];
        lpPlan = fairlaunchToLPPlan[fairlaunch];
    }
}

