// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title SimpleToken
 * @notice Standard ERC20 token with fixed supply
 * @dev No mint, no pause, no tax - perfectly safe for launchpads
 */
contract SimpleToken is ERC20, Ownable {
    uint8 private _decimals;

    constructor(
        string memory name_,
        string memory symbol_,
        uint256 totalSupply_,
        uint8 decimals_,
        address owner_
    ) ERC20(name_, symbol_) Ownable(owner_) {
        _decimals = decimals_;
        _mint(owner_, totalSupply_);
    }

    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }
}

/**
 * @title SimpleTokenFactory
 * @notice Factory for creating standard ERC20 tokens with platform fee
 * @dev Tokens created here are automatically whitelisted (skip SC scan)
 */
contract SimpleTokenFactory {
    // Platform treasury
    address public immutable treasury;

    // Token creation fee
    uint256 public creationFee;

    // Registry of created tokens
    mapping(address => bool) public isCreatedByPlatform;
    address[] public allCreatedTokens;

    // Events
    event TokenCreated(
        address indexed token,
        address indexed creator,
        string name,
        string symbol,
        uint256 totalSupply,
        uint8 decimals
    );

    event CreationFeeUpdated(uint256 oldFee, uint256 newFee);

    constructor(address treasury_, uint256 creationFee_) {
        require(treasury_ != address(0), "Invalid treasury");
        treasury = treasury_;
        creationFee = creationFee_;
    }

    /**
     * @notice Create a new ERC20 token
     * @param name Token name
     * @param symbol Token symbol
     * @param totalSupply Total supply (in wei, e.g., 1M * 10^18 for 1M tokens with 18 decimals)
     * @param decimals Token decimals (usually 18)
     * @return token Address of the created token
     */
    function createToken(
        string memory name,
        string memory symbol,
        uint256 totalSupply,
        uint8 decimals
    ) external payable returns (address token) {
        require(msg.value >= creationFee, "Insufficient creation fee");
        require(bytes(name).length > 0, "Name required");
        require(bytes(symbol).length > 0, "Symbol required");
        require(totalSupply > 0, "Supply must be > 0");
        require(decimals <= 18, "Decimals too high");

        // Deploy token (sender becomes owner)
        SimpleToken newToken = new SimpleToken(
            name,
            symbol,
            totalSupply,
            decimals,
            msg.sender
        );

        token = address(newToken);

        // Register token
        isCreatedByPlatform[token] = true;
        allCreatedTokens.push(token);

        // Send fee to treasury
        if (msg.value > 0) {
            (bool success, ) = treasury.call{value: msg.value}("");
            require(success, "Fee transfer failed");
        }

        emit TokenCreated(
            token,
            msg.sender,
            name,
            symbol,
            totalSupply,
            decimals
        );
    }

    /**
     * @notice Get total number of created tokens
     */
    function totalCreatedTokens() external view returns (uint256) {
        return allCreatedTokens.length;
    }

    /**
     * @notice Check if token was created by platform
     */
    function isPlatformToken(address token) external view returns (bool) {
        return isCreatedByPlatform[token];
    }
}

contracts/fairlaunch/TeamVesting.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

// Using explicit imports instead of separate console.sol
// import "hardhat/console.sol" // Only for debug

/**
 * @title TeamVesting
 * @notice Simple vesting contract for Fairlaunch team tokens
 * @dev Deployed by FairlaunchFactory. Tokens are pushed to this contract during Fairlaunch creation.
 */
contract TeamVesting is AccessControl, ReentrancyGuard {
    using SafeERC20 for IERC20;

    bytes32 public constant BENEFICIARY_ROLE = keccak256("BENEFICIARY_ROLE");

    // Immutable Config
    address public immutable token;
    uint256 public immutable startTime; // Reference time (usually Fairlaunch EndTime)

    struct VestingSchedule {
        uint256 durationBytes; // Duration in seconds from startTime
        uint256 amount;        // Amount to unlock
        bool claimed;
    }

    VestingSchedule[] public schedules;
    uint256 public totalVestingAmount;
    uint256 public totalClaimed;

    event TokensClaimed(address indexed beneficiary, uint256 amount);
    event ScheduleCreated(uint256 totalAmount, uint256 scheduleCount);

    error NoTokensToClaim();
    error Unauthorized();
    error InvalidSchedule();

    constructor(
        address _token,
        address _beneficiary,
        uint256 _startTime,
        uint256[] memory _durations,
        uint256[] memory _amounts,
        address _admin
    ) {
        require(_token != address(0), "Invalid token");
        require(_beneficiary != address(0), "Invalid beneficiary");
        require(_durations.length == _amounts.length, "Length mismatch");
        require(_durations.length > 0, "Empty schedule");

        token = _token;
        startTime = _startTime;

        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
        _grantRole(BENEFICIARY_ROLE, _beneficiary);

        uint256 total = 0;
        for (uint256 i = 0; i < _durations.length; i++) {
            schedules.push(VestingSchedule({
                durationBytes: _durations[i],
                amount: _amounts[i],
                claimed: false
            }));
            total += _amounts[i];
        }
        totalVestingAmount = total;

        emit ScheduleCreated(total, _durations.length);
    }

    /**
     * @notice Claim all unlocked tokens
     */
    function claim() external nonReentrant onlyRole(BENEFICIARY_ROLE) {
        uint256 claimable = 0;
        uint256 len = schedules.length;

        for (uint256 i = 0; i < len; i++) {
            if (!schedules[i].claimed) {
                if (block.timestamp >= startTime + schedules[i].durationBytes) {
                    schedules[i].claimed = true;
                    claimable += schedules[i].amount;
                }
            }
        }

        if (claimable == 0) revert NoTokensToClaim();

        totalClaimed += claimable;
        IERC20(token).safeTransfer(msg.sender, claimable);

        emit TokensClaimed(msg.sender, claimable);
    }

    /**
     * @notice Get claimable amount
     */
    function getClaimable() external view returns (uint256) {
        uint256 claimable = 0;
        uint256 len = schedules.length;

        for (uint256 i = 0; i < len; i++) {
            if (!schedules[i].claimed) {
                if (block.timestamp >= startTime + schedules[i].durationBytes) {
                    claimable += schedules[i].amount;
                }
            }
        }
        return claimable;
    }

    /**
     * @notice Get full schedule info
     */
    function getSchedule() external view returns (
        uint256[] memory times,
        uint256[] memory amounts,
        bool[] memory claimedStatus
    ) {
        uint256 len = schedules.length;
        times = new uint256[](len);
        amounts = new uint256[](len);
        claimedStatus = new bool[](len);

        for (uint i = 0; i < len; i++) {
            times[i] = startTime + schedules[i].durationBytes;
            amounts[i] = schedules[i].amount;
            claimedStatus[i] = schedules[i].claimed;
        }
    }
}

